import * as echarts from 'echarts';
export default {
  name: "Home",
  data() {
    return {};
  },
  mounted() {
    //页面元素渲染之后触发（和 created() 相反）
    const chartDom = document.getElementById('main');
    const myChart = echarts.init(chartDom);
    const option = {
      xAxis: {
        type: 'category',
        data: []
      },
      yAxis: {
        type: 'value'
      },
      series: [{
        data: [],
        type: 'line',
        smooth: true
      }, {
        data: [],
        type: 'bar',
        smooth: true
      }]
    };
    var pieChartDom = document.getElementById('pie');
    var myPieChart = echarts.init(pieChartDom);
    var pieOption = {
      title: {
        text: 'Referer of a Website',
        subtext: 'Fake Data',
        left: 'center'
      },
      tooltip: {
        trigger: 'item'
      },
      legend: {
        orient: 'vertical',
        left: 'left'
      },
      series: [{
        name: 'Access From',
        type: 'pie',
        radius: '50%',
        data: [{
          value: 1048,
          name: 'Search Engine'
        }, {
          value: 735,
          name: 'Direct'
        }, {
          value: 580,
          name: 'Email'
        }, {
          value: 484,
          name: 'Union Ads'
        }, {
          value: 300,
          name: 'Video Ads'
        }],
        emphasis: {
          itemStyle: {
            shadowBlur: 10,
            shadowOffsetX: 0,
            shadowColor: 'rgba(0, 0, 0, 0.5)'
          }
        }
      }]
    };
    this.request.get("/echarts/line").then(res => {
      //填空
      option.xAxis = res.data.x;
      option.series[0].data = res.data.y;
      option.series[1].data = res.data.y;
      myChart.setOption(option);
    });
    option && myChart.setOption(option);
    pieOption && myPieChart.setOption(pieOption);
  }
};
