import * as echarts from 'echarts';
export default {
  name: "Home",
  data() {
    return {};
  },
  mounted() {
    //页面元素渲染之后触发（和 created() 相反）
    const chartDom = document.getElementById('main');
    const myChart = echarts.init(chartDom);
    const option = {
      title: {
        text: '各季度用户注册数量统计',
        subtext: '趋势图',
        left: 'center'
      },
      xAxis: {
        type: 'category',
        data: ["第一季度", "第二季度", "第三季度", "第四季度"]
      },
      yAxis: {
        type: 'value'
      },
      series: [{
        data: [],
        type: 'line',
        smooth: true
      }, {
        data: [],
        type: 'bar',
        smooth: true
      }]
    };
    var pieChartDom = document.getElementById('pie');
    var myPieChart = echarts.init(pieChartDom);
    var pieOption = {
      title: {
        text: '各季度用户注册数量统计图',
        subtext: '数量对比',
        left: 'center'
      },
      tooltip: {
        trigger: 'item'
      },
      legend: {
        orient: 'vertical',
        left: 'left'
      },
      series: [{
        name: 'Access From',
        type: 'pie',
        radius: '60%',
        label: {
          //显示百分比
          normal: {
            show: true,
            position: 'inside',
            formatter: '{d}%',
            //模板变量有 {a}、{b}、{c}、{d}，分别表示系列名，数据名，数据值，百分比。{d}数据会根据value值计算百分比

            textStyle: {
              align: 'center',
              baseline: 'middle',
              fontFamily: '微软雅黑',
              fontSize: 13,
              fontWeight: 'normal',
              //也可以是数字400
              color: '#fff'
            }
          }
        },
        /*          data: [
                    { value: 1048, name: 'Search Engine' },
                    { value: 735, name: 'Direct' },
                    { value: 580, name: 'Email' },
                    { value: 484, name: 'Union Ads' },
                    { value: 300, name: 'Video Ads' }
                  ],*/

        data: [],
        //填这个，格式看上面注释
        emphasis: {
          itemStyle: {
            shadowBlur: 10,
            shadowOffsetX: 0,
            shadowColor: 'rgba(0, 0, 0, 0.5)'
          }
        }
      }]
    };
    this.request.get("/echarts/members").then(res => {
      //填空
      //option.xAxis.data = res.data.x
      option.series[0].data = res.data;
      option.series[1].data = res.data;
      //数据准备完毕后渲染
      myChart.setOption(option);
      pieOption.series[0].data = [{
        name: "第一季度",
        value: res.data[0]
      }, {
        name: "第二季度",
        value: res.data[1]
      }, {
        name: "第三季度",
        value: res.data[2]
      }, {
        name: "第四季度",
        value: res.data[3]
      }];
      myPieChart.setOption(pieOption);
    });
    option && myChart.setOption(option);
    pieOption && myPieChart.setOption(pieOption);
  }
};
